import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { connect } from 'react-redux';
import store from "./store";

class App extends Component{
    constructor(props) {
        super(props);

    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <img src={logo} className="App-logo" alt="logo" />
                    <p>
                        Edit <code>src/App.js</code> and save to reload.
                    </p>
                    <a
                        className="App-link"
                        href="https://reactjs.org"
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        Learn React
                    </a>
                </header>
            </div>
        );
    }
}

/*
 * No momento em que um determinado evento acontecer a função mapStateToProps irá
 * capturar dados do state irá passar para a props, a partir deste momento a função
 * de render irá rodar novamente pq ouve uma atualização do state.
 *
 * Isso tufo se deve a função connect que faz ligaçao entre o componente (classe) e a
 * store. Isso é feito pela função mapStateToProps.
 */

const mapStateToProps = (store) => {
    return {
        msg: store.clickReducer.msg
    }
};

export default connect(mapStateToProps)(App);
